const fixedCards = ['K♠', '8♣', 'J♥'];
const semiFixedPairs = [
  { value1: 'A♣', value2: '2♥' },
  { value1: '7♦', value2: '9♥' }
];

// Function to get the next card in the line of succession
const getNextCardInSuccession = (card) => {
