import React, { useState } from 'react';
import './App.css';
import birthdayCardMapping from './birthdayCardMapping.json';
import cardData from './cardData.json';
import Modal from 'react-modal';
import { ChakraProvider, Box, Heading, Select, Input, Flex, VStack } from '@chakra-ui/react';

// ... (keep all the existing constants and functions)

const App = () => {
  // ... (keep all the existing state and handler functions)

  return (
    <ChakraProvider>
      <Box className="App" p={5}>
        <VStack spacing={4} align="stretch">
          <Heading as="h1" size="xl" textAlign="center" mb={6}>
            Planetary Table
          </Heading>
          <Flex direction={{ base: "column", md: "row" }} justify="space-between" align="center" wrap="wrap">
            <Box mb={{ base: 4, md: 0 }} mr={{ md: 4 }} flex="1">
              <Select onChange={handlePlateChange} value={selectedPlate}>
                {spreads.map((_, index) => (
                  <option value={index} key={index}>
                    Plate {index}
                  </option>
                ))}
              </Select>
            </Box>
            <Box mb={{ base: 4, md: 0 }} mr={{ md: 4 }} flex="1">
              <Input
                type="date"
                value={birthdate}
                onChange={handleBirthdateChange}
                placeholder="Enter Birthdate"
              />
            </Box>
            <Box flex="1">
              <Select
                onChange={(e) => {
                  const selectedAction = e.target.value;
                  if (selectedAction === "weekly") {
                    handleWeeklyPlateClick();
                  } else if (selectedAction === "sevenWeekly") {
                    handleSevenWeeklyPlateClick();
                  } else if (selectedAction === "resetYearly") {
                    resetToYearlySpread();
                  }
                }}
              >
                <option value="">Select an Action</option>
                <option value="weekly">Show Weekly Plate</option>
                <option value="sevenWeekly">Show 7 Weekly Plate</option>
                <option value="resetYearly">Reset to Yearly Spread</option>
              </Select>
            </Box>
          </Flex>
        </VStack>

        {/* Keep the rest of the component's JSX unchanged */}
        <table border="1">
          {/* ... */}
        </table>

        {weeklyPlanetCards.length > 0 && (
          <div className="weekly-planet-cards" style={{ marginTop: '20px', textAlign: 'center' }}>
            {/* ... */}
          </div>
        )}

        <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          style={{
            content: {
              maxWidth: '600px',
              height:'600px',
              margin: '0 auto',
            },
          }}
        >
          {/* ... */}
        </Modal>
      </Box>
    </ChakraProvider>
  );
};

export default App;
