  { value1: 'A♣', value2: '2♥' },
  { value1: '7♦', value2: '9♥' }
];

// Function to get the next card in the line of succession
const getNextCardInSuccession = (card) => {
  const index = lineOfSuccession.indexOf(card);
  if (index !== -1 && index < lineOfSuccession.length - 1) {
